import React, { useState } from 'react';
import { summarizeTasks, queryTasks } from '../services/api';

export default function AIWidget({ tasks }) {
  const [summary,setSummary]=useState(''); const [q,setQ]=useState(''); const [ans,setAns]=useState(''); const [loading,setLoading]=useState(false);
  const doSumm = async ()=> {
    setLoading(true);
    try { const res = await summarizeTasks(tasks); setSummary(res.summary || JSON.stringify(res)); }
    catch(e){ setSummary('Error: '+e.message); } finally { setLoading(false); }
  };
  const doQuery = async ()=> {
    setLoading(true);
    try { const res = await queryTasks(q, tasks); setAns(res.answer || JSON.stringify(res)); }
    catch(e){ setAns('Error: '+e.message); } finally { setLoading(false); }
  };
  return (
    <div style={{marginTop:12}}>
      <h3>AI Assistant</h3>
      <div className="form-row">
        <button onClick={doSumm} disabled={loading}>Summarize Tasks</button>
        <input placeholder="Ask a question..." value={q} onChange={e=>setQ(e.target.value)} />
        <button onClick={doQuery} disabled={loading}>Ask AI</button>
      </div>
      {loading && <div>Thinking...</div>}
      <div><strong>Summary:</strong><pre>{summary}</pre></div>
      <div><strong>Answer:</strong><pre>{ans}</pre></div>
    </div>
  );
}
