import React, { useState, useEffect } from 'react';
import Column from './Column';
import { getTasks, createTask, updateTask, deleteTask } from '../services/api';

/**
 * Simple board without complex drag-n-drop library to keep code compact.
 * Drag-and-drop can be replaced with @dnd-kit implementation if desired.
 */
export default function TaskBoard({ project }) {
  const [tasks, setTasks] = useState([]);
  const [title,setTitle]=useState(''); const [desc,setDesc]=useState(''); const [status,setStatus]=useState('To Do');
  useEffect(()=>{ if(project) getTasks(project._id).then(setTasks); }, [project]);
  const refresh = ()=> project && getTasks(project._id).then(setTasks);
  const handleAdd = async ()=> {
    if(!title) return alert('Title required');
    await createTask(project._id, { title, description: desc, status });
    setTitle(''); setDesc(''); setStatus('To Do');
    refresh();
  };
  const handleEdit = async (task)=> {
    const newTitle = prompt('Title', task.title);
    if(newTitle===null) return;
    const newDesc = prompt('Description', task.description || '');
    const newStatus = prompt('Status (To Do,In Progress,Done)', task.status) || task.status;
    await updateTask(task._id, { title: newTitle, description: newDesc, status: newStatus });
    refresh();
  };
  const handleDelete = async (id)=> { if(!confirm('Delete?')) return; await deleteTask(id); refresh(); };
  const moveTask = async (task, newStatus) => { await updateTask(task._id, { status: newStatus }); refresh(); };

  const columns = ['To Do','In Progress','Done'];
  return project ? (
    <div>
      <h3>{project.name}</h3>
      <div className="form-row">
        <input placeholder="Task title" value={title} onChange={e=>setTitle(e.target.value)} />
        <input placeholder="Description" value={desc} onChange={e=>setDesc(e.target.value)} />
        <select value={status} onChange={e=>setStatus(e.target.value)}>
          {columns.map(c=> <option key={c} value={c}>{c}</option>)}
        </select>
        <button onClick={handleAdd}>Add Task</button>
      </div>

      <div className="board">
        {columns.map(col=>(
          <div key={col} style={{flex:1}}>
            <Column title={col} tasks={tasks.filter(t=>t.status===col)} onEdit={handleEdit} onDelete={handleDelete} />
            <div style={{marginTop:8}}>
              {tasks.filter(t=>t.status!==col).map(t=>(
                <button key={t._id} onClick={()=>moveTask(t,col)} style={{marginRight:6}}>Move "{t.title}" â†’ {col}</button>
              ))}
            </div>
          </div>
        ))}
      </div>
    </div>
  ) : <div>Select a project to view its board</div>;
}
