import React, { useState, useEffect } from 'react';
import { getProjects, createProject } from '../services/api';

export default function ProjectList({ onSelect }) {
  const [projects, setProjects] = useState([]);
  const [name, setName] = useState('');
  const [desc, setDesc] = useState('');
  useEffect(()=>{ getProjects().then(setProjects); }, []);
  const handleCreate = async () => {
    if(!name) return alert('Name required');
    const p = await createProject({ name, description: desc });
    setProjects([p, ...projects]);
    setName(''); setDesc('');
  };
  return (
    <div>
      <h3>Projects</h3>
      <div className="form-row">
        <input placeholder="Project name" value={name} onChange={e=>setName(e.target.value)} />
        <input placeholder="Description" value={desc} onChange={e=>setDesc(e.target.value)} />
        <button onClick={handleCreate}>Add Project</button>
      </div>
      <div className="projects">
        {projects.map(p => (
          <div key={p._id} className="project-card" onClick={()=>onSelect(p)}>{p.name}</div>
        ))}
      </div>
    </div>
  );
}
