import React, { useState } from 'react';
import ProjectList from './components/ProjectList';
import TaskBoard from './components/TaskBoard';
import AIWidget from './components/AIWidget';

export default function App(){
  const [project, setProject] = useState(null);
  const [currentTasks, setCurrentTasks] = useState([]);
  return (
    <div className="app">
      <div className="header">
        <h2>Project & Task Management System</h2>
        <div className="controls">Logged in as <strong>Candidate</strong></div>
      </div>

      <ProjectList onSelect={(p)=>{ setProject(p); /* optionally load tasks via TaskBoard */ }} />

      <div style={{display:'flex',gap:12,marginTop:12}}>
        <div style={{flex:3}}>
          <TaskBoard project={project} />
        </div>
        <div style={{flex:1}}>
          <AIWidget tasks={currentTasks} />
          <div style={{marginTop:12}}>
            <p><em>Note:</em> AI requires GEMINI_API_KEY in backend env to work.</p>
          </div>
        </div>
      </div>
    </div>
  );
}
