import React, { useState, useEffect } from 'react';
import Column from './Column';
import { getTasks, createTask, updateTask, deleteTask } from '../services/api';

export default function TaskBoard({ project }) {
  const [tasks, setTasks] = useState([]);
  const [title, setTitle] = useState('');
  const [desc, setDesc] = useState('');
  const [status, setStatus] = useState('To Do');

  const columns = ['To Do', 'In Progress', 'Done'];

  useEffect(() => {
    if (project) refreshTasks();
  }, [project]);

  const refreshTasks = async () => {
    const data = await getTasks(project._id);
    setTasks(data);
  };

  // --- Add Task ---
  const handleAdd = async () => {
    if (!title) return alert('Title required');
    await createTask(project._id, { title, description: desc, status });
    setTitle(''); setDesc(''); setStatus('To Do');
    refreshTasks();
  };

  // --- Edit Task ---
  const handleEdit = async (task) => {
    const newTitle = prompt('Edit Title', task.title);
    if (newTitle === null) return;
    const newDesc = prompt('Edit Description', task.description || '');
    const newStatus = prompt('Edit Status (To Do, In Progress, Done)', task.status) || task.status;
    await updateTask(task._id, { title: newTitle, description: newDesc, status: newStatus });
    refreshTasks();
  };

  // --- Delete Task ---
  const handleDelete = async (taskId) => {
    if (!window.confirm('Delete this task?')) return;
    await deleteTask(taskId);
    refreshTasks();
  };

  // --- Move Task between columns ---
  const moveTask = async (task, newStatus) => {
    if (task.status === newStatus) return;
    await updateTask(task._id, { status: newStatus });
    refreshTasks();
  };

  if (!project) return <div>Select a project to view its board</div>;

  return (
    <div>
      <h3>{project.name}</h3>

      {/* Add Task Form */}
      <div className="form-row" style={{ marginBottom: 12 }}>
        <input
          placeholder="Task title"
          value={title}
          onChange={(e) => setTitle(e.target.value)}
        />
        <input
          placeholder="Description"
          value={desc}
          onChange={(e) => setDesc(e.target.value)}
        />
        <select value={status} onChange={(e) => setStatus(e.target.value)}>
          {columns.map((c) => (
            <option key={c} value={c}>{c}</option>
          ))}
        </select>
        <button onClick={handleAdd}>Add Task</button>
      </div>

      {/* Kanban Board */}
      <div style={{ display: 'flex', gap: 12 }}>
        {columns.map((col) => (
          <div
            key={col}
            style={{
              flex: 1,
              border: '1px solid #ccc',
              borderRadius: 6,
              padding: 8,
              backgroundColor: '#f9f9f9',
              minHeight: 200,
            }}
          >
            <h4>{col}</h4>

            {/* Tasks in this column */}
            <Column
              title={col}
              tasks={tasks.filter((t) => t.status === col)}
              onEdit={handleEdit}
              onDelete={handleDelete}
            />

            {/* Move buttons for tasks in other columns */}
            <div style={{ marginTop: 8 }}>
              {tasks
                .filter((t) => t.status !== col)
                .map((t) => (
                  <button
                    key={t._id}
                    onClick={() => moveTask(t, col)}
                    style={{ marginRight: 6, marginBottom: 4, fontSize: '0.8em' }}
                  >
                    Move "{t.title}" â†’ {col}
                  </button>
                ))}
            </div>
          </div>
        ))}
      </div>
    </div>
  );
}
