import React, { useState, useEffect } from 'react';
import { getProjects, createProject, deleteProject } from '../services/api';

export default function ProjectList({ onSelect, onProjectDeleted }) {
  const [projects, setProjects] = useState([]);
  const [name, setName] = useState('');
  const [desc, setDesc] = useState('');

  // Load all projects on mount
  useEffect(() => {
    fetchProjects();
  }, []);

  const fetchProjects = async () => {
    const data = await getProjects();
    setProjects(data);
  };

  const handleCreate = async () => {
    if (!name) return alert('Name required');
    const p = await createProject({ name, description: desc });
    setProjects([p, ...projects]);
    setName('');
    setDesc('');
  };

  const handleDelete = async (id) => {
    if (window.confirm('Are you sure you want to delete this project?')) {
      await deleteProject(id);
      fetchProjects();
      if (onProjectDeleted) onProjectDeleted(); // Reset selected project if deleted
    }
  };

  return (
    <div>
      <h3>Projects</h3>
      <div className="form-row">
        <input
          placeholder="Project name"
          value={name}
          onChange={(e) => setName(e.target.value)}
        />
        <input
          placeholder="Description"
          value={desc}
          onChange={(e) => setDesc(e.target.value)}
        />
        <button onClick={handleCreate}>Add Project</button>
      </div>

      <div className="projects">
        {projects.map((p) => (
          <div key={p._id} className="project-card">
            <span
              style={{ cursor: 'pointer', marginRight: 10 }}
              onClick={() => onSelect(p)}
            >
              {p.name}
            </span>
            <button onClick={() => handleDelete(p._id)}>Delete</button>
          </div>
        ))}
      </div>
    </div>
  );
}
